import React, { useState, useEffect } from "react";
import axios from "axios";
import "./index.css";

const InventoryList = () => {
	const [inventory, setInventory] = useState([]);
	const [refreshing, setRefreshing] = useState(false);

	const refreshInventory = () => {
		setRefreshing(true);
		axios.get("http://127.0.0.1:5000/api/product").then((response) => {
			setInventory(response.data);
			setRefreshing(false);
		});
	};

	useEffect(() => {
		refreshInventory();
	}, []);

	const handleDeleteProduct = (productId) => {
		axios
			.delete(`http://127.0.0.1:5000/api/product/${productId}`)
			.then((response) => {
				console.log("Product deleted successfully:", response.data);
				// Refresh the inventory list
				refreshInventory();
			})
			.catch((error) => {
				console.error("Error deleting product:", error);
			});
	};

	return (
		<div className="inventory-list">
      <h3>Inventory List</h3>
      <button className="primary-button" onClick={refreshInventory} disabled={refreshing}>
        Refresh
      </button>
      {refreshing && <span>Refreshing...</span>}
      <ul>
        {inventory.map((item) => (
          <li key={item._id}>
            {/* ... */}
            <button className="danger-button" onClick={() => handleDeleteProduct(item._id)}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
	);
};

export default InventoryList;
